import React, { useMemo, useState } from 'react';

// Fotos já existentes
import team1 from '../assets/fotos/team1.jpg';
import team2 from '../assets/fotos/team2.jpg';
import team3 from '../assets/fotos/team3.jpg';
import team4 from '../assets/fotos/team4.jpg';
import team5 from '../assets/fotos/team5.jpg';
import team6 from '../assets/fotos/team6.jpg';
import team7 from '../assets/fotos/team7.jpg';
import team8 from '../assets/fotos/team8.jpg';

// Novas fotos enviadas
import m01 from '../assets/fotos/membros/membro_01.jpg';
import m02 from '../assets/fotos/membros/membro_02.jpg';
import m03 from '../assets/fotos/membros/membro_03.jpg';
import m04 from '../assets/fotos/membros/membro_04.jpg';
import m05 from '../assets/fotos/membros/membro_05.jpg';
import m06 from '../assets/fotos/membros/membro_06.jpg';
import m07 from '../assets/fotos/membros/membro_07.jpg';
import m08 from '../assets/fotos/membros/membro_08.jpg';
import m09 from '../assets/fotos/membros/membro_09.jpg';
import m10 from '../assets/fotos/membros/membro_10.jpg';

const SECTORES = [
  'Todos',
  'Chassis',
  'Suspensão e Freios',
  'Elétrica',
  'Back Office',
  'Powertrain e Drivetrain',
  'Aerodinâmica/Frame',
];

const MEMBERS = [
  { name: 'Membro 1', role: 'Engenheiro', sector: 'Chassis', photo: team1 },
  { name: 'Membro 2', role: 'Engenheiro', sector: 'Suspensão e Freios', photo: team2 },
  { name: 'Membro 3', role: 'Engenheiro', sector: 'Elétrica', photo: team3 },
  { name: 'Membro 4', role: 'Back Office', sector: 'Back Office', photo: team4 },
  { name: 'Membro 5', role: 'Powertrain', sector: 'Powertrain e Drivetrain', photo: team5 },
  { name: 'Membro 6', role: 'Aerodinâmica', sector: 'Aerodinâmica/Frame', photo: team6 },
  { name: 'Membro 7', role: 'Chassis', sector: 'Chassis', photo: team7 },
  { name: 'Membro 8', role: 'Elétrica', sector: 'Elétrica', photo: team8 },

  { name: 'Membro 9',  role: 'Chassis',       sector: 'Chassis', photo: m01 },
  { name: 'Membro 10', role: 'Back Office',   sector: 'Back Office', photo: m02 },
  { name: 'Membro 11', role: 'Powertrain',    sector: 'Powertrain e Drivetrain', photo: m03 },
  { name: 'Membro 12', role: 'Elétrica',      sector: 'Elétrica', photo: m04 },
  { name: 'Membro 13', role: 'Suspensão',     sector: 'Suspensão e Freios', photo: m05 },
  { name: 'Membro 14', role: 'Aerodinâmica',  sector: 'Aerodinâmica/Frame', photo: m06 },
  { name: 'Membro 15', role: 'Chassis',       sector: 'Chassis', photo: m07 },
  { name: 'Membro 16', role: 'Elétrica',      sector: 'Elétrica', photo: m08 },
  { name: 'Membro 17', role: 'Back Office',   sector: 'Back Office', photo: m09 },
  { name: 'Membro 18', role: 'Suspensão',     sector: 'Suspensão e Freios', photo: m10 },
];

const EquipePage = () => {
  const [active, setActive] = useState('Todos');

  const filtered = useMemo(() => {
    if (active === 'Todos') return MEMBERS;
    return MEMBERS.filter((m) => m.sector === active);
  }, [active]);

  return (
    <section className="section team-section" style={{ paddingTop: '2rem' }}>
      <h2 className="section-title">Nossa Equipe</h2>

      {/* Texto centralizado */}
      <p
        className="team-note"
        style={{
          margin: '0.25rem 0 1rem',
          fontSize: '1rem',
          textAlign: 'center', // apenas centraliza o texto
        }}
      >
        Filtre por setor para conhecer quem cuida de cada área do nosso carro.
      </p>

      {/* Filtros centralizados (mantendo estilos originais) */}
      <div
        className="filters"
        style={{
          display: 'flex',
          flexWrap: 'wrap',
          gap: '0.5rem',
          justifyContent: 'center',
          marginBottom: '1.5rem',
        }}
      >
        {SECTORES.map((s) => (
          <button
            key={s}
            onClick={() => setActive(s)}
            aria-pressed={active === s}
            className={`btn ${active === s ? 'btn-primary' : 'btn-outline'}`}
            style={{
              borderRadius: '999px',
              padding: '0.5rem 0.9rem',
              border: '1px solid var(--accent, #9b1c31)',
              background: active === s ? 'var(--accent, #9b1c31)' : 'transparent',
              color: active === s ? '#fff' : 'var(--text, #ddd)',
              cursor: 'pointer',
              fontSize: '0.9rem',
            }}
          >
            {s}
          </button>
        ))}
      </div>

      {/* Grid de membros */}
      <div className="team-grid">
        {filtered.map((m, idx) => (
          <div className="team-card" key={idx}>
            <img src={m.photo} alt={m.name} className="avatar-img" />
            <h3>{m.name}</h3>
            <p className="muted">{m.role}</p>
            <span
              className="tag"
              style={{
                display: 'inline-block',
                marginTop: '0.4rem',
                padding: '0.2rem 0.6rem',
                borderRadius: '999px',
                border: '1px solid #555',
                fontSize: '0.75rem',
              }}
            >
              {m.sector}
            </span>
          </div>
        ))}
      </div>
    </section>
  );
};

export default EquipePage;
